import React from "react";
import {withRouter} from 'react-router-dom';
import classes from './index.module.css'
import {MDBBtn, MDBCard, MDBIcon, MDBModal, MDBModalBody,MDBModalHeader} from "mdbreact";
import {cancelSyn, handleSyn} from "../../../page/RRH/Component/speech-syn";


class StepPanelReact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            modal1: false,
        };

    }
    toggle = nr => () => {
        cancelSyn()
        let modalNumber = 'modal' + nr
        this.setState({
            [modalNumber]: !this.state[modalNumber],
            answer:''
        });
    }

    render() {

        return (
            <div className='d-flex justify-content-around'>
                <div>{this.props.data.format}</div>
                <div className='ml-3'>
                    <MDBIcon style={{cursor:'pointer'}} className='grey-text' far icon="edit" toggle={this.toggle(1)}/>
                </div>

                <MDBModal isOpen={this.state.modal1} toggle={this.toggle(1)} centered size="lg">
                    <MDBModalHeader>Edit Step</MDBModalHeader>

                    <MDBModalBody className="text-center">
                        <div></div>


                    </MDBModalBody>
                </MDBModal>


            </div>
        );
    }
}
export const StepPanel = withRouter(StepPanelReact);
