import React from "react";
import {withRouter} from 'react-router-dom';
import classes from './index.module.css';
import {nodeurl, url} from "../../../tool/fetch-help";
import {handleSyn} from "../../../page/RRH/Component/speech-syn";


class ProblemPanelReact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            backend:'',
        };

    }
    componentDidMount() {
        const option={
            method:'GET',
            mode: 'cors',
            credentials: 'include',
            headers: {
                'content-type': 'application/json',
            }
        };
        fetch(`${nodeurl}/problem/${this.props.id}`,option)
            .then(response=>response.json())
            .then(res=>{
                this.setState({
                    backend:res,
                });
            })
    }


    render() {
        return (
           <div
               className={classes.Card}
               onClick={()=>{
                   this.props.history.push(`/problem/${this.state.backend.id}`);
               }}
           >
               <div>
                   {this.state.backend.id}
                   {this.state.backend.name}
                   {this.state.backend.description}
                   {this.state.backend.parameter_number}
                   {this.state.backend.start_format}
               </div>
           </div>
        );
    }
}
export const ProblemPanel = withRouter(ProblemPanelReact);
