import React from "react";
import {withRouter} from 'react-router-dom';
import classes from './index.module.css'
import {Nav} from "../../page/Nav";
import {nodeurl} from "../../tool/fetch-help";
import { MDBContainer, MDBStepper, MDBStep, MDBIcon, MDBRow, MDBCol, MDBBtn } from "mdbreact";


class ProblemDetailReact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            render:0
        };

    }
    async componentDidMount() {
        const problem={
            method:'GET',
            headers: {
                'content-type': 'application/json',
            }
        };
        const select={
            method:'POST',
            headers: {
                'content-type': 'application/json',
            },
            body:JSON.stringify({problem_ID: this.props.match.params.id})
        };
        await fetch(`${nodeurl}/problem/${this.props.match.params.id}`,problem)
            .then(response=>response.json())
            .then(res=>{
                this.setState({
                    problem:res[0],
                });
            });
        await fetch(`${nodeurl}/second_level_parameter`,select)
            .then(response=>response.json())
            .then(res=>{
                this.setState({
                    param:res,

                });
            });
        await fetch(`${nodeurl}/step`,select)
            .then(response=>response.json())
            .then(res=>{
                this.setState({
                    step:res,
                    render:1
                });
            })
    }

    render() {
        if(this.state.render ===1){
            return (
                <div>
                    <Nav/>
                    <div className='cell-wall'>
                        <div className='cell-membrane'>
                            <div className ='mt-3'>Problem Info</div>
                            <div className={classes.Card}>
                                <div>{`Name:`} {this.state.problem.name}</div>
                                <div>{this.state.problem.description}</div>
                                <div>{`Param Num:`} {this.state.problem.parameter_number}</div>
                            </div>
                            <div className='row'>
                                <div className='col-8'>
                                    <div>Step</div>
                                    <MDBStepper vertical className='p-0'>
                                        <MDBStep className="completed">
                                            <a>
                                                <span className="circle">1</span>
                                                <span className="label">Start Format</span>
                                            </a>
                                        </MDBStep>
                                        <MDBStep className="active">
                                            <a>
                                                <span className="circle">2</span>
                                                <span className="label">Second step</span>
                                            </a>
                                            <div className="step-content grey lighten-4">
                                                <p>
                                                    Lorem ipsum dolor sit amet, consectetur adipisicing elit. Esse
                                                    cupiditate voluptate facere iusto quaerat vitae excepturi,
                                                    accusantium ut aliquam repellat atque nesciunt nostrum
                                                    similique. Inventore nostrum ut, nobis porro sapiente.
                                                </p>
                                                <p>
                                                    Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                                                    Dolore error excepturi veniam nemo repellendus, distinctio
                                                    soluta vitae at sit saepe. Optio eaque quia excepturi adipisci
                                                    pariatur totam, atque odit fugiat.
                                                </p>
                                                <p>
                                                    Deserunt voluptatem illum quae nisi soluta eum perferendis
                                                    nesciunt asperiores tempore saepe reiciendis, vero quod a
                                                    dolor corporis natus qui magni quas fuga rem excepturi
                                                    laboriosam. Quisquam expedita ab fugiat.
                                                </p>
                                            </div>
                                        </MDBStep>
                                        <MDBStep className="warning">
                                            <a href="#!">
            <span className="circle">
              <MDBIcon icon="exclamation-triangle" />
            </span>
                                                <span className="label">Third step</span>
                                            </a>
                                        </MDBStep>
                                        <MDBStep>
                                            <a href="#!">
                                                <span className="circle">4</span>
                                                <span className="label">Fourth step</span>
                                            </a>
                                        </MDBStep>
                                        <MDBRow className="mt-1">
                                            <MDBCol md="12" className="text-right">
                                                <MDBBtn flat>Cancel</MDBBtn>
                                                <MDBBtn color="primary">Next </MDBBtn>
                                            </MDBCol>
                                        </MDBRow>
                                    </MDBStepper>
                                    <div className={classes.step}>{this.state.problem.start_format}</div>
                                    {
                                        this.state.step.map((item, index) => {
                                            return <div key={index}>{item.id}</div>;
                                        })
                                    }

                                </div>
                                <div className='col-4'>
                                    <div>Second Level Parameter</div>
                                    {
                                        this.state.param.map((item, index) => {
                                            return <div key={index}>{item.id}</div>;
                                        })
                                    }
                                </div>
                            </div>
                        </div>
                    </div>

                </div>
            );
        }
        else{
            return null
        }

    }
}
export const ProblemDetail = withRouter(ProblemDetailReact);
