import React from 'react';
import classes from '../../ComplexNumber/Page/index.module.css';
import classes2 from './index.module.css'

import {AskQuestion} from "../Component/ask-question";
import {MDBBtn, MDBCard, MDBCol, MDBIcon, MDBModalBody, MDBRow} from "mdbreact";
import {handleSyn} from "../Component/speech-syn";
import {cancelSyn} from "../Component/speech-syn";
import {url} from "../../../tool/fetch-help";
import Joyride from 'react-joyride';

const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
const recognition = new SpeechRecognition();

recognition.continous = true;
recognition.interimResults = true;
recognition.lang = 'en-US';

export class End extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            listening: false,
            speechState:'Click to start...',
            hints:'',
            backendjson:'',
            question:'',
            backend:'',
            answer:'',
            tag:'',
            type:0,
            render:0,
            index:0,
            feedback:'',
            steps: [
                {
                    target: ".question",
                    content: "We ask your questions to make sure if you understand our story. Once your answer is right, there will be a new question"
                },
                {
                    target: ".input",
                    content: "Input your answer here"
                }
            ]
        };
        this.toggleListen = this.toggleListen.bind(this)
        this.handleListen = this.handleListen.bind(this)
        this.searchAnswer = this.searchAnswer.bind(this)
        this.onKeyup = this.onKeyup.bind(this)
    }
    componentDidMount() {
        const option={
            method:'GET',
            headers: {
                'content-type': 'application/json',
            }
        }
        fetch(`${url}/feedback/question`,option)
            .then(response=>response.json())
            .then(question=>{
                this.setState({
                    backend:question,
                    render:1
                })
                handleSyn(this.state.backend.content[0].question.replace('?', '.'))
            })
    }
    componentWillMount() {
        handleSyn(this.state.answer)
    }


    searchAnswer=(value)=>{
        //cancelSyn()
        console.log(this.state.backend.content[this.state.index].id)
        const option={
            method:'POST',
            headers: {
                'content-type': 'application/json',
            },
            body:JSON.stringify({
                question_id:this.state.backend.content[this.state.index].id,
                answer:value,
                // "question":this.state.backend.content[this.state.index].id,
                // "answer":value
            })
        };
        fetch(`${url}/analyse_answer/a1`,option)
            .then(response=>response.json())
            .then(answer=>{
                this.setState({
                    backendjson:answer
                })
                switch (answer.type) {
                    case '1':
                        this.setState({
                            feedback:'',
                            index:this.state.index + 1,
                            type:1,
                            tag:'Yes! You got it.'
                        });
                        handleSyn('Yes! You got it.');
                        if(this.state.index <= this.state.backend.content.length){
                            handleSyn(this.state.backend.content[this.state.index].question.replace('?', '.'))
                        }
                        break;
                    case '2':
                        this.setState({
                            tag:'Is that what you are answering?',
                            feedback:answer.answer,
                            type:2
                        });
                        handleSyn('Is that what you are answering');
                        handleSyn((answer.answer.replace('?', '.')));

                        break;
                    case '3':
                        this.setState({
                            feedback:answer.answer,
                            tag:'No.',
                            type:3
                        });
                        handleSyn('No, the answer is');
                        handleSyn((answer.answer.replace('?', '.')));
                }
            })
    }

    //--------------Speech Recognition--------------
    toggleListen() {
        cancelSyn()
        this.setState({
            listening: !this.state.listening
        }, this.handleListen)
    }

    handleListen() {

        console.log('listening?', this.state.listening)

        if (this.state.listening) {
            recognition.start();
            recognition.onend = () => {
                this.setState({
                    speechState:'...continue listening...'
                });
                recognition.start()
            }

        } else {
            recognition.stop();
            recognition.onend = () => {
                this.setState({
                    speechState:'Click to start...'
                });
            }
        }

        recognition.onstart = () => {
            this.setState({
                speechState:'Listening...Click to pause'
            });
        };

        let finalTranscript = '';
        recognition.onresult = event => {
            let interimTranscript = '';

            for (let i = event.resultIndex; i < event.results.length; i++) {
                const transcript = event.results[i][0].transcript;
                if (event.results[i].isFinal) finalTranscript += transcript + ' ';
                else interimTranscript += transcript;
            }

            this.setState({
                answer:finalTranscript
            });
            document.getElementById('interim').innerHTML = interimTranscript;
            document.getElementById('final').value = finalTranscript;

            //-------------------------COMMANDS------------------------------------

            const transcriptArr = finalTranscript.split(' ');
            const stopCmd = transcriptArr.slice(-3, -1);
            console.log('stopCmd', stopCmd);

            if (stopCmd[0] === 'stop' && stopCmd[1] === 'listening'){
                recognition.stop();
                recognition.onend = () => {
                    console.log('Stopped listening per command');
                    const finalText = transcriptArr.slice(0, -3).join(' ');
                    document.getElementById('final').value = finalText
                }
            }
        }

        //-----------------------------------------------------------------------

        recognition.onerror = event => {
            console.log("Error occurred in recognition: " + event.error)
        }

    }

    onKeyup(e) {
        if(e.keyCode === 13) {
            this.searchAnswer(this.state.answer)}
    }

    render() {
        if (this.state.render === 1){
            const { steps } = this.state;
            //console.log(this.state.backend.content[0])
            return (
                <div className={`${classes.body} align-content-center w-100 h-100 text-center`}>
                    <Joyride
                        steps={steps}
                        continuous={true}
                        scrollToFirstStep={true}
                        scrollToSteps={false}
                        styles={{
                            options: {
                                primaryColor: '#54B948',
                                zIndex: 1000,
                            }
                        }}
                    />
                    <AskQuestion/>

                    <div className="d-flex align-items-baseline justify-content-center">
                        <div className={classes.title1}>
                            Q & A
                        </div>
                    </div>
                    <MDBRow>
                        <MDBCol size="2">

                        </MDBCol>
                        <MDBCol size="8">
                            <div className={classes2.ph}>
                                Great! Now you read the story and we have some question for you to answer.
                            </div>
                            <br/>

                            <div className={classes2.border} style={{borderWidth:'1px'}}>

                                <p className={`${classes2.ph} question`}>
                                    {this.state.index <= this.state.backend.content.length?
                                        this.state.backend.content[this.state.index].question
                                        :'All questions have been done!'}
                                </p>

                                <div className="d-flex justify-content-center align-content-start mt-3 mb-3">
                                    <div className="flex-grow-1">
                                        <input
                                            id='final'
                                            className={`form-control form-control-lg input ${classes.searchInput}`}
                                            placeholder="Answer question here"
                                            style={{
                                                borderStyle:'solid',
                                                borderWidth:'1px',
                                                borderColor:'#81c784',
                                                borderRadius:'15px',
                                                fontFamily:'\'Rajdhani\', sans-serif',
                                                fontSize:'22px',
                                            }}
                                            onChange={(e) => {
                                                const str=e.target.value
                                                this.setState({
                                                    answer: str
                                                });
                                            }}
                                            onKeyDown={this.onKeyup}
                                        />
                                    </div>
                                    <div className="ml-3">
                                        <MDBBtn
                                            tag="a" floating color="green" style={{margin:'6px'}}
                                            onClick={()=>{this.searchAnswer(this.state.answer)}}
                                        >
                                            <MDBIcon icon="question" />
                                        </MDBBtn>
                                    </div>
                                    <div className="ml-1">
                                        <MDBBtn
                                            tag="a" floating color="purple lighten-2" style={{margin:'6px'}}
                                            onClick={this.toggleListen}
                                        >
                                            <MDBIcon icon="microphone" />
                                        </MDBBtn>

                                    </div>
                                </div>
                                <div>
                                    <div className={classes2.body2}>{this.state.speechState}</div>
                                    <div id='interim'></div>
                                </div>
                                <div className="mt-3">
                                    <MDBCard
                                        size="8"
                                        text="white"
                                        className="py-1 px-3 w-100"
                                        style={{boxShadow:'none', borderRadius:'0px',backgroundColor:'#e8eaf6'}}
                                    >

                                        <p className={classes2.pb2}>{this.state.tag}</p>

                                        <p className={classes2.pb2}>{this.state.feedback}</p>
                                        <div>
                                            {this.state.type === 2? (
                                                <div className="d-flex justify-content-center align-items-center">

                                                    <MDBBtn
                                                        tag="a" floating className="green"
                                                        onClick={()=>{
                                                            cancelSyn()
                                                            this.setState({
                                                                type:1,
                                                                tag:'Yes, you got it',
                                                                feedback:this.state.backendjson.answer
                                                            })
                                                            handleSyn('Yes, you got it.')
                                                        }}
                                                    >
                                                        <MDBIcon icon="check" />
                                                    </MDBBtn>
                                                    <MDBBtn
                                                        tag="a" floating className="red lighten-1"
                                                        onClick={()=>{
                                                            cancelSyn()
                                                            this.setState({
                                                                type:1,
                                                                tag:'No',
                                                                feedback:this.state.backendjson.answer
                                                            })
                                                            handleSyn('No, the answer is')
                                                            handleSyn((this.state.backendjson.answer.replace('?', '.')));
                                                        }}
                                                    >
                                                        <MDBIcon icon="times" />
                                                    </MDBBtn>
                                                </div>
                                            ):(null)
                                            }
                                        </div>
                                    </MDBCard>
                                </div>
                            </div>
                            <br/>
                        </MDBCol>

                    </MDBRow>
                    <div style={{padding:'30px 0 250px 0'}}>
                        <div
                            onClick={() => {this.props.history.goBack();}}
                            className={classes2.a}>&laquo; Previous
                        </div>
                        <div
                            onClick={() => {this.props.history.push('/rrh/cover');}}
                            className={classes2.a}>Again &raquo;
                        </div>
                    </div>
                </div>
            );
        }
        else{
            return(
                null
            );
        }
    }
}
