                    <div className={classes.title1}>
                        FOIL: &nbsp;
                    </div>
                    <div className={classes.title2}>
                        Multiplying Binomials
                    </div>
                </div>

                <MDBRow>
                    <MDBCol size="2">
                    </MDBCol>
                    <MDBCol size="8" className={classes.font3}>
                        <Video url='https://myedmaster.oss-us-east-1.aliyuncs.com/FOILvid2.mp4'/>
                    </MDBCol>
                </MDBRow>
                <MDBRow>
                    <MDBCol size="2">

